import * as React from 'react';
import { Table } from 'react-bootstrap';


function HeroesList() {
    const [heroList, setHerosList] = React.useState([]);
    async function doFetch(search: string) {
        const res = await fetch(`http://localhost:5000/superheroes/search/${search}`);
        const data = await res.json();
        setHerosList(data);
    }
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>FullName</th>
                    <th>Avatar</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>1</td>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td>Image</td>
                </tr>
                <tr>
                    <td>2</td>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>Images</td>
                </tr>
            </tbody>
        </Table>
    );
}

export default HeroesList;
