import React, { useState } from 'react';
import './App.css';
import Hero from './components/Hero';
import HeroesList from './components/HeroesList';
import SearchBar from './components/SearchBar';
import SideBar from './components/SideBar';




function App() {
  const [heroList, setHerosList] = useState([]);

  function doFetch(search: string) {
    fetch(`https://http://localhost:5000/${search}`)
      .then(res => res.json())
      .then(data => {
        setHerosList(data.results);
      }).catch(err => console.log(err));
  }



  return (
    <div className="App">
      <div className='lftPanel'>
        <SearchBar />
        <HeroesList />
      </div>
      <SideBar />
    </div>
  );
}
export default App;
