import * as React from 'react';
import 'react-app-polyfill/ie11';
import { Formik, Field, Form, FormikHelpers } from 'formik';
import { useDispatch } from 'react-redux';
import { Table } from 'react-bootstrap';


interface Values {
    Search: string;
}

async function doFetch(search: string) {
    const res = await fetch(`http://localhost:5000/superheroes/search/${search}`);
    const data = await res.json();
    return data;
}

const SearchAndDisplayResults = () => {

    const dispatch = useDispatch();
    return (
        <div>
            <h1>Signup</h1>
            <Formik
                initialValues={{
                    Search: '',
                }}
                onSubmit={(
                    values: Values,
                    { setSubmitting }: FormikHelpers<Values>
                ) => {
                    dispatch(setSearchResults(doFetch(values.Search)));
                    setTimeout(() => {
                        alert(JSON.stringify(values, null, 2));
                        setSubmitting(false);
                    }, 500);
                }}
            >
                <Form>
                    <label htmlFor="Search">Search</label>
                    <Field id="Search" name="Search" placeholder="Batman" />
                    <button type="submit">Submit</button>
                </Form>
            </Formik>
            <div>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>FullName</th>
                            <th>Avatar</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>1</td>
                            <td>Mark</td>
                            <td>Otto</td>
                            <td>Image</td>
                        </tr>
                        <tr>
                            <td>2</td>
                            <td>Jacob</td>
                            <td>Thornton</td>
                            <td>Images</td>
                        </tr>
                    </tbody>
                </Table>
            </div>
        </div>
    );
};
export default SearchAndDisplayResults;
